// This code is designed to run in an n8n Function node and utilizes 
// the latest JSON payload structure provided by the user.

const inputData = $input.first().json.entry; 

// --- CONFIGURATION ---
const SELLER_GST_NUMBER = '33BBFPG9210H2ZZ'; 

if (!inputData) {
    throw new Error("CRITICAL: Input data 'entry' object is missing from the payload.");
}

// Map the dynamic input data to the structure the HTML template expects.
const data = {
    invoiceNumber: inputData.invoiceNumber,
    invoiceDate: inputData.entryDate,
    reference: inputData.rentalId,
    modeOfPayment: 'N/A', 
    companyId: {
        companyName: inputData.companyId.companyName,
        billingAddress: `${inputData.companyId.billingAddress}, ${inputData.companyId.city}, ${inputData.companyId.state}, ${inputData.companyId.pincode}`,
        gstNo: inputData.companyId.gstNo,
        contactPersons: inputData.companyId.contactPersons || [],
    },
    deliveryAddress: `${inputData.companyId.billingAddress}, ${inputData.companyId.city}, ${inputData.companyId.state}, ${inputData.companyId.pincode}`, 
    products: [] 
};

// Machine and Configuration Pointers
const machine = inputData.machineId || {};
const machineBasePrice = (typeof machine.basePrice === 'number') ? machine.basePrice : 0;
const machineGstType = machine.gstType || [];
const machineHsn = machine.hsn || '';
const entryA3Config = inputData.a3Config || {};
const entryA4Config = inputData.a4Config || {};
const entryA5Config = inputData.a5Config || {};

/**
 * Helper: safeNumeric - returns number or null if not numeric
 */
function safeNumeric(value) {
    if (value === undefined || value === null) return null;
    const n = Number(value);
    return Number.isFinite(n) ? n : null;
}

/**
 * Calculates a single line item for extra copies based on meter readings.
 * Returns a product object if activity (totalCopies) > 0, otherwise returns null.
 */
function calculateLineItem(configName, entryConfig = {}, machineSizeConfig = {}, copyType) {
    let typeName = '';
    let oldKey = '';
    let newKey = '';
    let freeKey = '';
    let extraAmtKey = '';

    // Determine config keys based on copyType
    if (copyType === 'bw') {
        typeName = 'B/W Copy';
        oldKey = 'bwOldCount';
        newKey = 'bwNewCount';
        freeKey = 'freeCopiesBw';
        extraAmtKey = 'extraAmountBw';
    } else if (copyType === 'color') {
        typeName = 'Color Copy';
        oldKey = 'colorOldCount';
        newKey = 'colorNewCount';
        freeKey = 'freeCopiesColor';
        extraAmtKey = 'extraAmountColor';
    } else if (copyType === 'colorScanning') {
        typeName = 'Color Scanning';
        oldKey = 'colorScanningOldCount';
        newKey = 'colorScanningNewCount';
        freeKey = 'freeCopiesColorScanning';
        extraAmtKey = 'extraAmountColorScanning';
    } else {
        return null;
    }

    // Read counts from entryConfig; preserve zeros
    const oldCountRaw = (entryConfig && Object.prototype.hasOwnProperty.call(entryConfig, oldKey)) ? entryConfig[oldKey] : null;
    const newCountRaw = (entryConfig && Object.prototype.hasOwnProperty.call(entryConfig, newKey)) ? entryConfig[newKey] : null;

    // Convert to numeric where possible, otherwise keep null
    const oldCountNum = safeNumeric(oldCountRaw);
    const newCountNum = safeNumeric(newCountRaw);

    // If both counts are missing, skip
    if (oldCountNum === null && newCountNum === null) return null;

    // Free copies and extra amount (defaults to 0)
    const freeCopies = safeNumeric(machineSizeConfig && machineSizeConfig[freeKey]) || 0;
    const extraAmountPerCopy = safeNumeric(machineSizeConfig && machineSizeConfig[extraAmtKey]) || 0;

    // Calculate difference (copies made since last reading) - only if both numeric
    const totalCopies = (newCountNum !== null && oldCountNum !== null) ? (newCountNum - oldCountNum) : 0;

    // Calculate extra paper count: Copies made above zero, less the free copies.
    const extraPaperCount = (totalCopies > 0) ? Math.max(0, totalCopies - freeCopies) : 0;

    // Total taxable amount for this line item (Extra Paper Count * Rate Per Item)
    const totalLineCharge = extraPaperCount * extraAmountPerCopy;

    // Show line if any copies were made (totalCopies > 0)
    if (totalCopies > 0) {
        return {
            productName: `COPIES CHARGE - ${configName} ${typeName} (Used: ${totalCopies}, Free: ${freeCopies})`,
            quantity: extraPaperCount, // QTY is now the Extra Paper Count (0 in sample)
            rate: extraAmountPerCopy, // RATE PER ITEM is the cost per extra copy
            productId: {
                hsn: machineHsn,
                gstType: machineGstType
            },
            oldCount: oldCountNum,
            newCount: newCountNum,
            extraPaperCount: extraPaperCount,
        };
    }
    return null;
}

// 1. Add the Base Price (Ensures 'None' counts for the required columns)
data.products.push({
    productName: `MACHINE RENTAL - ${machine.modelName || ''} (S/N: ${machine.serialNo || ''}) - Base Price`,
    quantity: 1,
    rate: machineBasePrice, 
    productId: {
        hsn: machineHsn,
        gstType: machineGstType
    },
    // Explicitly set to 'None' to match the visual requirement for the Base Price row
    oldCount: 'None', 
    newCount: 'None',
    extraPaperCount: 'None',
});

// 2. Calculate and Add Extra Copy Charges for all configs (A3, A4, A5, B/W, Color, Scanning)
const configTypes = [
    { size: 'A3', entry: entryA3Config, machine: machine.a3Config || {} },
    { size: 'A4', entry: entryA4Config, machine: machine.a4Config || {} },
    { size: 'A5', entry: entryA5Config, machine: machine.a5Config || {} }
];
const copyTypes = ['bw', 'color', 'colorScanning'];

configTypes.forEach(conf => {
    copyTypes.forEach(type => {
        const item = calculateLineItem(conf.size, conf.entry, conf.machine, type);
        if (item) data.products.push(item);
    });
});


// --- 1. Helper Functions for Data Formatting ---

function formatDate(isoString) {
    if (!isoString) return 'N/A';
    const date = new Date(isoString);
    if (isNaN(date.getTime())) return 'N/A';
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return `${year}-${month}-${day}`;
}

function formatTime(isoString) {
    if (!isoString) return 'N/A';
    const date = new Date(isoString);
    if (isNaN(date.getTime())) return 'N/A';
    return date.toTimeString().split(' ')[0];
}

function formatCurrency(num) {
    return (typeof num === 'number') ? num.toFixed(2) : '0.00';
}

function formatCurrencyNoDecimal(num) {
    return (typeof num === 'number') ? Math.round(num) : '0';
}

function numberToWords(num) {
    const number = Number(num);
    if (isNaN(number) || number === null) return '';
    if (number === 0) return 'Zero Rupees Only /-';
    const a = ['', 'One ', 'Two ', 'Three ', 'Four ', 'Five ', 'Six ', 'Seven ', 'Eight ', 'Nine ', 'Ten ', 'Eleven ', 'Twelve ', 'Thirteen ', 'Fourteen ', 'Fifteen ', 'Sixteen ', 'Seventeen ', 'Eighteen ', 'Nineteen '];
    const b = ['', '', 'Twenty', 'Thirty', 'Forty', 'Fifty', 'Sixty', 'Seventy', 'Eighty', 'Ninety'];
    const n = ('000000000' + Math.round(number)).substr(-9).match(/^(\d{2})(\d{2})(\d{2})(\d{1})(\d{2})$/);
    if (!n) return '';
    let str = '';
    str += (n[1] != 0) ? (a[Number(n[1])] || b[n[1][0]] + ' ' + a[n[1][1]]) + 'Crore ' : '';
    str += (n[2] != 0) ? (a[Number(n[2])] || b[n[2][0]] + ' ' + a[n[2][1]]) + 'Lakh ' : '';
    str += (n[3] != 0) ? (a[Number(n[3])] || b[n[3][0]] + ' ' + a[n[3][1]]) + 'Thousand ' : '';
    str += (n[4] != 0) ? (a[Number(n[4])] || b[n[4][0]] + ' ' + a[n[4][1]]) + 'Hundred ' : '';
    str += (n[5] != 0) ? ((str != '') ? 'And ' : '') + (a[Number(n[5])] || b[n[5][0]] + ' ' + a[n[5][1]]) : '';
    return str.trim() + ' Rupees Only /-';
}

// --- 2. DYNAMIC & RELIABLE CALCULATIONS ---
let recalculatedSubtotal = 0;
let totalIgstAmount = 0;
let totalCgstAmount = 0;
let totalSgstAmount = 0;
let totalTaxAmount = 0;
let representativeIgstRate = 0;
let representativeCgstRate = 0;
let representativeSgstRate = 0;

data.products.forEach(p => {
    // QTY is now Extra Paper Count. For Base Price, QTY is 1.
    const qty = (typeof p.quantity === 'number') ? p.quantity : Number(p.quantity) || 0; 
    const rate = (typeof p.rate === 'number') ? p.rate : Number(p.rate) || 0;
    const itemTaxableAmount = rate * qty; // Total Taxable Amount for the line
    recalculatedSubtotal += itemTaxableAmount;

    if (p.productId && p.productId.gstType && Array.isArray(p.productId.gstType)) {
        p.productId.gstType.forEach(taxComponent => {
            const pct = safeNumeric(taxComponent.gstPercentage) || 0;
            const itemComponentTax = itemTaxableAmount * (pct / 100);
            totalTaxAmount += itemComponentTax;

            if (taxComponent.gstType === 'IGST') {
                totalIgstAmount += itemComponentTax;
                representativeIgstRate = Math.max(representativeIgstRate, pct);
            } else if (taxComponent.gstType === 'CGST') {
                totalCgstAmount += itemComponentTax;
                representativeCgstRate = Math.max(representativeCgstRate, pct);
            } else if (taxComponent.gstType === 'SGST') {
                totalSgstAmount += itemComponentTax;
                representativeSgstRate = Math.max(representativeSgstRate, pct);
            }
        });
    }
});

const finalTaxableValue = recalculatedSubtotal;
const finalGrandTotal = finalTaxableValue + totalTaxAmount;
const amountInWords = numberToWords(finalGrandTotal);


// --- 3. Dynamic HTML Generation for Product Rows ---
const productRows = data.products.map(p => {
    // QTY is now Extra Paper Count. For Base Price, QTY is 1.
    const qty = (typeof p.quantity === 'number') ? p.quantity : Number(p.quantity) || 0; 
    const rate = (typeof p.rate === 'number') ? p.rate : Number(p.rate) || 0;
    const itemTaxableAmount = rate * qty;  
    let itemTotalTax = 0;
    let totalGstPercentage = 0;

    if (p.productId && p.productId.gstType && Array.isArray(p.productId.gstType)) {
        p.productId.gstType.forEach(taxComponent => {
            const pct = safeNumeric(taxComponent.gstPercentage) || 0;
            itemTotalTax += itemTaxableAmount * (pct / 100);
            totalGstPercentage += pct;
        });
    }

    // Custom fields for display 
    const oldC = p.oldCount || 'None';
    const newC = p.newCount || 'None';
    // Logic for EXTRA PAPER COUNT: 
    // If it's the Base Price row ('None'), keep 'None'. 
    // If it's a numeric 0 (zero extra copies), display 'None'.
    // Otherwise, display the number of extra copies (QTY).
    let extraP = p.extraPaperCount;

    if (extraP === 'None') {
        extraP = 'None';
    } else {
        extraP = (extraP === 0 || extraP === '0') ? 'None' : extraP;
    }

    // For the extra copies lines, QTY and EXTRA PAPER COUNT should be the same, 
    // but the Base Price QTY is 1, while its EXTRA PAPER COUNT is 'None'.
    const displayQty = (p.productName.includes('Base Price')) ? 1 : (qty || 'None');

    return `
      <tr>
        <td style="text-align: left;">${p.productName || p.productName === '' ? (p.productName) : (p.description || 'N/A')}</td>

        <td>${(p.productId && p.productId.hsn) ? p.productId.hsn : 'N/A'}</td>

        <td>${displayQty}</td>

        <td>${formatCurrency(rate)}</td>

        <td>${oldC}</td>

        <td>${newC}</td>

        <td>${extraP}</td>

        <td>${totalGstPercentage}%</td>

        <td>${formatCurrencyNoDecimal(itemTotalTax)}</td>

        <td>${formatCurrencyNoDecimal(itemTaxableAmount)}</td>
      </tr>`;
}).join('');


// --- 4. The Complete HTML Template (Adapted to be dynamic) ---
const finalHtml = `
