// --- 1. Helper Functions for Data Formatting ---

function formatDate(isoString) {
  if (!isoString) return 'N/A';
  const date = new Date(isoString);
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  return `${year}-${month}-${day}`;
}

function formatTime(isoString) {
    if (!isoString) return 'N/A';
    const date = new Date(isoString);
    return date.toTimeString().split(' ')[0];
}

function formatCurrency(num) {
    return (typeof num === 'number') ? num.toFixed(2) : '0.00';
}

function formatCurrencyNoDecimal(num) {
    return (typeof num === 'number') ? Math.round(num) : '0';
}

function numberToWords(num) {
    const number = Number(num);
    if (isNaN(number) || number === null) return '';
    if (number === 0) return 'Zero Rupees /-';
    const a = ['', 'One ', 'Two ', 'Three ', 'Four ', 'Five ', 'Six ', 'Seven ', 'Eight ', 'Nine ', 'Ten ', 'Eleven ', 'Twelve ', 'Thirteen ', 'Fourteen ', 'Fifteen ', 'Sixteen ', 'Seventeen ', 'Eighteen ', 'Nineteen '];
    const b = ['', '', 'Twenty', 'Thirty', 'Forty', 'Fifty', 'Sixty', 'Seventy', 'Eighty', 'Ninety'];
    const n = ('000000000' + Math.round(number)).substr(-9).match(/^(\d{2})(\d{2})(\d{2})(\d{1})(\d{2})$/);
    if (!n) return '';
    let str = '';
    str += (n[1] != 0) ? (a[Number(n[1])] || b[n[1][0]] + ' ' + a[n[1][1]]) + 'Crore ' : '';
    str += (n[2] != 0) ? (a[Number(n[2])] || b[n[2][0]] + ' ' + a[n[2][1]]) + 'Lakh ' : '';
    str += (n[3] != 0) ? (a[Number(n[3])] || b[n[3][0]] + ' ' + a[n[3][1]]) + 'Thousand ' : '';
    str += (n[4] != 0) ? (a[Number(n[4])] || b[n[4][0]] + ' ' + a[n[4][1]]) + 'Hundred ' : '';
    str += (n[5] != 0) ? ((str != '') ? '' : '') + (a[Number(n[5])] || b[n[5][0]] + ' ' + a[n[5][1]]) : '';
    return str.trim() + ' Rupees /-';
}

// --- 2. DYNAMIC & RELIABLE CALCULATIONS ---
let recalculatedSubtotal = 0;
let totalTaxAmount = 0;
let totalCgstAmount = 0;
let totalSgstAmount = 0;

data.products.forEach(p => {
    const itemTaxableAmount = p.rate * p.quantity;
    recalculatedSubtotal += itemTaxableAmount;

    if (p.productId.gstType && Array.isArray(p.productId.gstType)) {
        p.productId.gstType.forEach(taxComponent => {
            const itemComponentTax = itemTaxableAmount * (taxComponent.gstPercentage / 100);
            totalTaxAmount += itemComponentTax;

            if (taxComponent.gstType === 'CGST') {
                totalCgstAmount += itemComponentTax;
            } else if (taxComponent.gstType === 'SGST') {
                totalSgstAmount += itemComponentTax;
            }
        });
    }
});

const finalTaxableValue = recalculatedSubtotal;
const finalGrandTotal = recalculatedSubtotal + totalTaxAmount;
const amountInWords = numberToWords(finalGrandTotal);

let representativeCgstRate = 0;
let representativeSgstRate = 0;
if (data.products.length > 0 && data.products[0].productId.gstType) {
    const firstProductTaxes = data.products[0].productId.gstType;
    const cgstInfo = firstProductTaxes.find(t => t.gstType === 'CGST');
    const sgstInfo = firstProductTaxes.find(t => t.gstType === 'SGST');
    if (cgstInfo) representativeCgstRate = cgstInfo.gstPercentage;
    if (sgstInfo) representativeSgstRate = sgstInfo.gstPercentage;
}


// --- 3. Dynamic HTML Generation for Product Rows ---
// The .map() function below is a loop. It will create a new table row <tr> for EACH product in the products array.
const productRows = data.products.map(p => {
    // These values are calculated for each row individually.
    const itemTaxableAmount = p.rate * p.quantity;
    let itemTotalTax = 0;
    let totalGstPercentage = 0;

    if (p.productId.gstType && Array.isArray(p.productId.gstType)) {
        p.productId.gstType.forEach(taxComponent => {
            itemTotalTax += itemTaxableAmount * (taxComponent.gstPercentage / 100);
            totalGstPercentage += taxComponent.gstPercentage;
        });
    }

    const itemNetRate = itemTaxableAmount + itemTotalTax;

    // This HTML block is created for each product, with its specific values mapped directly from the payload.
    return `
      <tr>
        <!-- Field: productName -->
        <td style="text-align: left;">${p.productName || 'N/A'}</td>

        <!-- Field: hsn (from nested productId object) -->
        <td>${p.productId.hsn || 'N/A'}</td>

        <!-- Field: quantity (top-level, invoice-specific) -->
        <td>${p.quantity}</td>

        <!-- Field: rate (top-level, invoice-specific) -->
        <td>${formatCurrency(p.rate)}</td>

        <!-- Field: totalAmount (CALCULATED, not from payload) -->
        <td>${formatCurrency(itemTaxableAmount)}</td>

        <!-- Field: gst (CALCULATED from nested gstType array) -->
        <td>${totalGstPercentage}%</td>

        <!-- Field: Tax Rate (CALCULATED tax amount for the row) -->
        <td>${formatCurrencyNoDecimal(itemTotalTax)}</td>

        <!-- Field: Net Rate (CALCULATED net total for the row) -->
        <td>${formatCurrencyNoDecimal(itemNetRate)}</td>
      </tr>`;
}).join('');

