// Load the puppeteer library
const puppeteer = require('puppeteer');

// Define our main function
async function generatePdf(html) {
  if (typeof html !== 'string' || html.length < 10) {
    throw new Error('The input HTML is empty or invalid. Please check the "Set" node and the input connection.');
  }

  let browser;
  try {
    // --- THIS IS THE KEY FIX ---
    // Launch the browser with the necessary arguments for a stable Docker environment
    browser = await puppeteer.launch({
      headless: true, // It's good practice to be explicit
      args: [
        '--no-sandbox',
        '--disable-setuid-sandbox',
        '--disable-dev-shm-usage', // Prevents hanging by avoiding limited shared memory
        '--disable-gpu'            // Disables GPU hardware acceleration
      ]
    });
    // --- END OF FIX ---

    const page = await browser.newPage();
    await page.setContent(html, { waitUntil: 'networkidle0' });

    const pdfBuffer = await page.pdf({
      format: 'A4',
      printBackground: true,
      margin: { top: '20px', right: '20px', bottom: '20px', left: '20px' }
    });

    return pdfBuffer;

  } catch (error) {
    // Adding more detailed logging to see where it fails
    console.error('An error occurred during PDF Generation with Puppeteer:', error);
    throw error;
  } finally {
    if (browser) {
      await browser.close();
    }
  }
}

// --- Main execution starts here ---

const htmlContent = $input.item.json.html;

const rawPdfData = await generatePdf(htmlContent);

const base64String = Buffer.from(rawPdfData).toString('base64');

return [
  {
    json: {
      data: base64String
    }
  }
];
